package com.imshenik.java.chapter4;

public class ZigZag {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		while(true){
			System.out.println("|");
			System.out.println(" |");
			System.out.println("  |");
			System.out.println("   |");
			System.out.println("    |");
			System.out.println("     |");
			System.out.println("      |");
			System.out.println("       |");
			System.out.println("        |");
			System.out.println("       |");
			System.out.println("      |");
			System.out.println("     |");
			System.out.println("    |");
			System.out.println("   |");
			System.out.println("  |");
			System.out.println(" |");
			System.out.println("|");
			
		}
	}

}
